INSERT INTO "course" ("id", "title", "description", "points", "createdat", "updatedat") VALUES (1, 'Cryptology', 'Cryptology is the mathematics, such as number theory, and the application of formulas and algorithms, that underpin cryptography and cryptanalysis. Since the cryptanalysis concepts are highly specialized and complex, we concentrate here only on some of the key mathematical concepts behind cryptography.', '4', '26T08:30:57.138Z', '26T08:30:57.138Z');
INSERT INTO "course" ("id", "title", "description", "points", "createdat", "updatedat") VALUES (3, 'Real-Time Systems', 'A real-time system is a time-bound system which has well-defined, fixed time constraints. Processing must be done within the defined constraints or the system will fail. They either are event-driven or time-sharing.', '4.5', '26T08:30:57.138Z', '26T08:30:57.138Z');
